package com.armeniopinto.stress.control;

import java.util.Map;

/**
 * An asynchronous event generated by one of the platform's components.
 * 
 * @author armenio.pinto
 */
public class Event extends Message {

	public static final String MESSAGE_TYPE = "Event";

	public Event(final Map<String, Object> data) {
		super(MESSAGE_TYPE, data);
	}

	@Override
	public final int hashCode() {
		return super.hashCode();
	}

	@Override
	public final boolean equals(final Object obj) {
		boolean result = false;
		if (obj instanceof Event) {
			result = ((Event) obj).canEqual(this) && super.equals(obj);
		}

		return result;
	}

	@Override
	protected final boolean canEqual(final Object obj) {
		return obj instanceof Event;
	}

}
